import React, { useState } from 'react';
import styled from 'styled-components';

// サンプルのフラッシュカードデータ
const flashcardData = [
  {
    term: "CPU",
    definition: "Central Processing Unitの略。コンピュータの中心的な処理を行う装置。"
  },
  {
    term: "RAM",
    definition: "Random Access Memoryの略。コンピュータの主記憶装置。"
  },
  {
    term: "OS",
    definition: "Operating Systemの略。コンピュータの基本ソフトウェア。"
  }
];

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  min-height: 100vh;
  padding: 2rem;
  background-color: #f5f5f5;
`;

const Title = styled.h1`
  color: #333;
  margin-bottom: 2rem;
`;

const FlashcardContainer = styled.div`
  perspective: 1000px;
  width: 300px;
  height: 200px;
  margin: 1rem;
`;

const Flashcard = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
  transform-style: preserve-3d;
  transition: transform 0.6s;
  transform: ${props => props.isFlipped ? 'rotateY(180deg)' : 'rotateY(0)'};
  cursor: pointer;
`;

const CardFace = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  backface-visibility: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 1rem;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  background-color: white;
  font-size: 1.2rem;
  text-align: center;
`;

const CardBack = styled(CardFace)`
  transform: rotateY(180deg);
  background-color: #f8f9fa;
`;

const NavigationButtons = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const Button = styled.button`
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 5px;
  background-color: #007bff;
  color: white;
  cursor: pointer;
  &:hover {
    background-color: #0056b3;
  }
`;

function App() {
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [isFlipped, setIsFlipped] = useState(false);

  const handleNext = () => {
    setIsFlipped(false);
    setCurrentCardIndex((prev) => (prev + 1) % flashcardData.length);
  };

  const handlePrevious = () => {
    setIsFlipped(false);
    setCurrentCardIndex((prev) => (prev - 1 + flashcardData.length) % flashcardData.length);
  };

  return (
    <AppContainer>
      <Title>ITパスポート フラッシュカード</Title>
      <FlashcardContainer>
        <Flashcard isFlipped={isFlipped} onClick={() => setIsFlipped(!isFlipped)}>
          <CardFace>{flashcardData[currentCardIndex].term}</CardFace>
          <CardBack>{flashcardData[currentCardIndex].definition}</CardBack>
        </Flashcard>
      </FlashcardContainer>
      <NavigationButtons>
        <Button onClick={handlePrevious}>前へ</Button>
        <Button onClick={handleNext}>次へ</Button>
      </NavigationButtons>
    </AppContainer>
  );
}

export default App;
